import React, {useEffect, useState} from 'react';
import { fetchEmails, triggerFetch, triggerProcess, updateDraft } from './api';
import Dashboard from './components/Dashboard';

function App(){
  const [emails, setEmails] = useState([]);
  useEffect(()=> { load(); }, []);
  function load(){ fetchEmails().then(setEmails); }
  return (
    <div className="app">
      <header className="topbar">
        <h1>AI Support Assistant â€” Linkenite</h1>
        <div>
          <button onClick={()=> { triggerFetch().then(()=>alert('Fetch started (backend)')); }}>Fetch Emails</button>
          <button onClick={()=> { triggerProcess().then(()=>load()); }}>Process (Sentiment & Drafts)</button>
          <button onClick={load}>Refresh</button>
        </div>
      </header>
      <main>
        <Dashboard emails={emails} onUpdate={load} onSaveDraft={(id,draft)=>updateDraft(id,draft).then(load)}/>
      </main>
    </div>
  );
}
export default App;
