import React, { useEffect, useState } from 'react';
import EmailCard from './EmailCard';
import Analytics from './Analytics';

export default function Dashboard() {
  const [emails, setEmails] = useState([]);
  const [loading, setLoading] = useState(false);
  const [processing, setProcessing] = useState(false);

  // Fetch emails from backend
  const fetchEmails = async () => {
    try {
      const res = await fetch("http://localhost:8000/emails");
      const data = await res.json();
      setEmails(data);
    } catch (err) {
      console.error(err);
      alert("Failed to fetch emails");
    }
  };

  // Trigger backend fetch-emails (IMAP/SMTP)
  const triggerFetchEmails = async () => {
    setLoading(true);
    try {
      const res = await fetch("http://localhost:8000/fetch-emails", { method: "POST" });
      const data = await res.json();
      alert("Email fetch started!");
      console.log(data);
    } catch (err) {
      console.error(err);
      alert("Failed to start fetching emails");
    }
    setLoading(false);
  };

  // Trigger backend process-all
  const triggerProcessAll = async () => {
    setProcessing(true);
    try {
      const res = await fetch("http://localhost:8000/process-all");
      const data = await res.json();
      alert("All emails processed!");
      fetchEmails(); // refresh after processing
    } catch (err) {
      console.error(err);
      alert("Failed to process emails");
    }
    setProcessing(false);
  };

  // Save draft to backend
  const onSaveDraft = async (id, draft) => {
    try {
      const res = await fetch(`http://localhost:8000/email/${id}/update-draft`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ draft_reply: draft }),
      });
      if (!res.ok) throw new Error("Failed to save draft");
      alert("Draft saved!");
      fetchEmails(); // refresh email list after save
    } catch (err) {
      console.error(err);
      alert("Error saving draft");
    }
  };

  useEffect(() => {
    fetchEmails();
    const interval = setInterval(fetchEmails, 60000); // Auto-refresh every 60s
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="dashboard">
      <h1>AI Support Assistant Dashboard</h1>

      <div style={{ marginBottom: '20px', display: 'flex', gap: '10px' }}>
        <button onClick={triggerFetchEmails} disabled={loading}>
          {loading ? "Fetching..." : "Fetch Emails"}
        </button>

        <button onClick={triggerProcessAll} disabled={processing}>
          {processing ? "Processing..." : "Process All Emails"}
        </button>
      </div>

      <Analytics emails={emails} />

      <div className="email-list">
        {emails.length === 0 && <div className="no-emails">No emails yet.</div>}
        {emails.map(e => (
          <EmailCard key={e.id} email={e} onSave={draft => onSaveDraft(e.id, draft)} />
        ))}
      </div>
    </div>
  );
}
